<?php

namespace Runalyze\Calculation\Route;

/**
 * Generated by PHPUnit_SkeletonGenerator 1.2.0 on 2015-01-17 at 19:26:39.
 */
class GeohashLineTest extends \PHPUnit_Framework_TestCase {


	public function testShortenAndExtend()
    {

        $GeohashFromDB ='u1xjnxhj49qr|u1xjnxhjr7wb|u1xjnxhm6zkm|u1xjnxhmqqg2|u1xjnxhmrvmd|u1xjnxhtk8w1|u1xjnxhv447q|u1xjnxjh9tgm|u1xjnxjhe53c|u1xjnxjk4qyn|u1xjnxjef6c2|u1xjnxjg35cs';
        $longHashes = explode('|', $GeohashFromDB);

        $short = 'u1xjnxhj49qr|r7wb|m6zkm|qqg2|rvmd|tk8w1|v447q|jh9tgm|e53c|k4qyn|ef6c2|g35cs';
        $shortHashes = explode('|', $short);

        $shortened = GeohashLine::shorten($longHashes);
        $this->assertEquals($shortHashes, $shortened);

        $extended = GeohashLine::extend($shortened);
        $this->assertEquals($longHashes, $extended);
    }

}
