<?php

namespace Runalyze\Data\Weather;

/**
 * Generated by PHPUnit_SkeletonGenerator 1.2.0 on 2014-09-02 at 14:44:59.
 */
class ForecastTest extends \PHPUnit_Framework_TestCase {
	public function testEmptyLocation() {
		$Forecast = new Forecast(new Openweathermap, new Location);
		$object = $Forecast->object();

		$this->assertTrue( $object->isEmpty() );
	}

	public function testLocationByName() {
		$Location = new Location();
		$Location->setLocationName('Berlin, de');

		$Forecast = new Forecast(new Openweathermap, $Location);
		$object = $Forecast->object();

		$this->assertFalse( $object->isEmpty() );
	}

	public function testLocationByTime() {
		$Location = new Location();
		$Location->setLocationName('Berlin, de');
		$Location->setTimestamp( time() - 7*86500 );

		$Forecast = new Forecast(new Openweathermap, $Location);
		$object = $Forecast->object();

		$this->assertFalse( $object->isEmpty() );
	}

	public function testLocationByPosition() {
		$Location = new Location();
		$Location->setPosition(49.9, 7.77);

		$Forecast = new Forecast(new Openweathermap, $Location);
		$object = $Forecast->object();

		$this->assertFalse( $object->isEmpty() );
	}

}
